mapper.select:
SELECT COUNT(*)
FROM APPUSR
WHERE POST_SEQ = #{postSeq} AND USR_ID = #{usrId}

mapper.insert:
INSERT INTO APPUSR (
	POST_SEQ
	, USR_ID
	, GRD_SEQ
	, TEAM_SEQ
	, INS_DT
) VALUES (
	#{postSeq}
	, #{usrId}
	, #{grdSeq}
	, #{teamSeq}
	, SYSDATE()
)

mapper.select:
SELECT IFNULL(MAX(TEAM_SEQ),0)+1
FROM APPTEAM
WHERE POST_SEQ = #{postSeq}

mapper.insert:
INSERT INTO APPTEAM (
	POST_SEQ
	, TEAM_SEQ
	, TEAM_NM
	, CEO_ID
) VALUES (
	#{postSeq}
	, #{teamSeq}
	, #{teamNm}
	, #{ceoId}
)
