mapper.select:
SELECT PM.POST_SEQ , PM.USR_ID , PM.OPEN_YN , PM.POST_TIT , PM.POST_CONT , PM.POST_AREA1 , PM.POST_AREA2 , PM.HASH_TAG , PM.PER_YN , PM.TEAM_YN , PM.TEAM_MIN_CNT , PM.TEAM_MAX_CNT , PM.END_DT , PM.END_YN , (SELECT COUNT(*) FROM POSTGOOD PG WHERE PG.POST_SEQ = PM.POST_SEQ) AS GOOD_CNT , (SELECT CASE WHEN COUNT(*) > 0 THEN 'Y' ELSE 'N' END FROM POSTGOOD PG WHERE PG.USR_ID = #{sessionId} AND PG.POST_SEQ = PM.POST_SEQ) AS GOOD_YN , UM.PROFILE FROM USRMST UM RIGHT OUTER JOIN POSTMST PM ON UM.USR_ID = PM.USR_ID
INNER JOIN MATELIST ML ON PM.USR_ID = ML.MATE_ID AND ML.USR_ID = #{sessionId}
WHERE PM.END_YN = 'N' AND PM.OPEN_YN = 'Y' AND PM.POST_AREA1 = #{postArea1}
PM.POST_AREA2 LIKE '%${item}%'
AND PM.PER_YN = #{perYn} AND PM.TEAM_YN = #{teamYn}
AND PM.USR_ID = #{usrId}
AND PM.TEAM_MIN_CNT = #{teamMinCnt}
AND PM.TEAM_MAX_CNT = #{teamMaxCnt}
AND PM.END_DT = #{endDt}

SELECT POST_SEQ, IMG_SEQ, ORI_FILE, SVR_FILE
FROM POSTIMG
WHERE POST_SEQ = #{postSeq}

SELECT COUNT(*)
FROM POSTGOOD
WHERE POST_SEQ = #{postSeq}

mapper.update:
UPDATE POSTMST SET OPEN_YN = #{openYn}
			 , POST_TIT = #{postTit}
			 , POST_CONT = #{postCont}
			 , POST_AREA1  = #{postArea1}
			 , POST_AREA2 = #{postArea2}
			 , HASH_TAG = #{hashTag}
			 , PER_YN = #{perYn}
			 , TEAM_YN = #{teamYn}
			 , TEAM_MIN_CNT = #{teamMinCnt}
			 , TEAM_MAX_CNT = #{teamMaxCnt}
			 , END_DT = #{endDt}
			 , UPD_DT = NOW()
WHERE POST_SEQ = #{postSeq}
